public void selectionSort(int[] a, int n) {

	if(n <= 1) {
		return;
	}

	for(int i = 0; i < n - 1; i++){
		int min = i;

		// æ‰¾å‡ºå‰©ä¸‹çš„æœªæ’åºåŒºé—´çš„æœ€å°å€¼çš„ä¸‹æ ‡
