    public <T> void partitionArray(T[] elements, Predicate<T>... conditions) {
        int p = 0;
        for (Predicate<T> condition : conditions) {
            int q = elements.length - 1;
            while (p != q) {
                if (!condition.test(elements[p])) {
                    if (condition.test(elements[q])) {
                        T tmp = elements[p];
                        elements[p] = elements[q];
                        elements[q] = tmp;
                    } else {
                        q--;
                    }
                } else {
                    p++;
                }
            }
        }
    }

