				else if ((mid + 1)*(mid + 1) > val) break;
				low = mid;
			}
			else high = mid;
		}
	}
	else mid = 0;

	double sMid, sLow, sHigh;
	double pos = 1;
	sLow = mid, sHigh = mid + pos;
	sMid = (sLow + sHigh) / 2;

	for (int i = 0; i < 7; i++)
	{		
		pos /= 10;
		while (sLow < sHigh)
		{
			sMid = (sLow + sHigh) / 2;
			if (sMid*sMid < val)
			{
				if ((sMid + pos)*(sMid + pos) > val) break;
				else if ((sMid + pos)*(sMid + pos) == val) return sMid + pos;
				sLow = sMid;
			}
			else if (sMid*sMid > val) sHigh = sMid;
			else return sMid;
		}
		sLow = sMid, sHigh = sMid + pos; 
	}
	return sMid;
}
