vector<vector<int>> levelOrder(TreeNode* root) 
    {
        vector<vector<int>> vecRes;
        if (!root)
        {
            return vecRes;
        }
        vector<int> vecTemp;
        queue<TreeNode *> queueT1;
        queueT1.push(root);
        int nQueueSize = queueT1.size();
        while (nQueueSize > 0)
        {
            for (int i = 0; i< nQueueSize; ++i)
            {
                TreeNode * pQueue = queueT1.front();
                vecTemp.push_back(pQueue->val);
                queueT1.pop();
                if (pQueue->left)
                {
                    queueT1.push(pQueue->left);
                }
                if (pQueue->right)
                {
                    queueT1.push(pQueue->right);
                }
            }
            nQueueSize = queueT1.size();
            vecRes.push_back(vecTemp);
            vecTemp.clear();
        }
        return vecRes;
    }
