 public int maxDepth(TreeNode root) {
        if(root == null){
            return 0;
        }
        TreeNode node = root;
        Queue<TreeNode> queue = new LinkedList<>();
        queue.offer(node);
        int front = 0;// é˜Ÿå¤´
        int rear = queue.size();// é˜Ÿå°¾
        int floor = 1;
        while (!queue.isEmpty()){
            node = queue.poll();
            front++;
            if(node.left != null){
                queue.offer(node.left);
            }
            if(node.right != null){
                queue.offer(node.right);
            }
            if(front == rear){// è¿›å…¥ä¸‹ä¸€å±‚
                front = 0;
                rear = queue.size();
                floor++;// å±‚æ•°+1
            }
        }
        return floor;
    }

