public static int getTreeFloor(Node root) {
        if(root == null){
            return 0;
        }
        if(root.left == null &amp;&amp; root.right == null){
            return 1;
        }
        //è·å–å³å­æ ‘é«˜åº¦
        int rFloor = getTreeFloor(root.right);
        //è·å–å·¦å­æ ‘é«˜åº¦
        int lFloor = getTreeFloor(root.left);
        return Math.max(rFloor,lFloor)+1;
    }

