class Solution {
    public int findContentChildren(int[] g, int[] s) {
        if(0==s.length) return 0;
        Arrays.sort(g);
        Arrays.sort(s);
        int gIndex=g.length-1,sIndex=s.length-1;
        int result=0;
        for(int i=sIndex;i>=0&amp;&amp; gIndex>=0;i--){
            while(gIndex>=0 &amp;&amp; s[i]<g[gIndex] ) gIndex--;
            if(gIndex>=0){
                result+=1;
            }
