public class Array {
    private String[] data;
    private int len;
    private int cap;

    // æ„é€ å™¨
    public Array(int capacity) {
        data = new String[capacity];
        len = 0;
        cap = capacity;
    }

    public boolean append(String value){
        if (len == cap) {
            return false;
        }
        data[len++] = value;
        return true;
    }

    public boolean insert(int index, String value) {
        if (len == cap) {
            return false;
        }
        if (index < 0 || index > len) {
            return false;
        }
        for (int i = len; i > index; i--) {
            data[i] = data[i-1];
        }
        data[index] = value;
        len++;
        return true;
    }

    public boolean delete(int index, String value) {
        if (len == 0) {
            return false;
        }
        if (index < 0 || index > len) {
            return false;
        }
        for (int i = index; i <= len - 1; i++) {
            data[i - 1] = data[i];
        }
        len--;
        return true;
    }
}
