def download(src, imgId):
    img_dir = &#39;D:\\Personal\\Code\\Jupyter\\数据分析实战___极客时间\\picture\\&#39;
    # 对imgId做字符串处理，删除字符串中的？
    imgId = re.sub(r&#39;[?]&#39;, &#39;&#39;, str(imgId))
