        save_dir += p_id + &#39;.jpg&#39;
    else:
        save_dir += str(p_id) + &#39;.jpg&#39;
    try:
        pic = requests.get(src, timeout=10)
        with open(save_dir, &#39;wb&#39;) as fout:
