static inline unsigned int ep_item_poll(struct epitem *epi, poll_table *pt)
{
	pt-&amp;gt;_key = epi-&amp;gt;event.events;

	return epi-&amp;gt;ffd.file-&amp;gt;f_op-&amp;gt;poll(epi-&amp;gt;ffd.file, pt) &amp;amp; epi-&amp;gt;event.events;
}

