public class InputTextEasy {
    private StringBuilder text = new StringBuilder();
    public String getText() {
        return text.toString();
    }
    public int getTextLength() {
        return text.toString().length();
    }
    public void append(String input) {
        text.append(input);
    }
    public SnapshotEasy createSnapshot() {
        return new SnapshotEasy(this.text.length());
    }
    public void restoreSnapshot(SnapshotEasy snapshot) {
        this.text.delete(snapshot.getLength(), this.text.length());
    }
}
