public class SnapshotEasy {
    private int length;
    public SnapshotEasy(int length) {
        this.length = length;
    }
    public int getLength() {
        return length;
    }
}
//---
@Slf4j
public class SnapshotEasyHolder {
    private Stack<SnapshotEasy> snapshots = new Stack<>();
    public SnapshotEasy popSnapshot() {
        if (snapshots.isEmpty()) {
            log.warn(&#34;snapshots is empty, cannot pop...&#34;);
            return null;
        }
        return snapshots.pop();
    }
    public void pushSnapshot(SnapshotEasy snapshot) {
        log.debug(&#34;push val: [&#34; + snapshot.getLength() + &#34;]&#34;);
        snapshots.push(snapshot);
    }
}
