	u32 pid = bpf_get_current_pid_tgid();
	umode_t mode = args-&gt;mode;
	char * filename = (char *)args-&gt;filename;
	data.pid = pid ;
	data.mode = mode; 
	data.filename = filename;

	events.perf_submit(args, &amp;data, sizeof(data)); 
	return 0;
}


&quot;&quot;&quot;

