const { filterTR, mapTR, composeReducer } = (() =&gt; {
	const applyTypeForFunction = (fn, type) =&gt; ({ type, fn });
	const filterTR = (fn) =&gt; {
		return applyTypeForFunction(fn, &#34;filter&#34;);
	};
	const mapTR = (fn) =&gt; {
		return applyTypeForFunction(fn, &#34;map&#34;);
	};

	const composeReducer = (inputs, fns) =&gt; {
		return inputs.reduce((sum, input) =&gt; {
			const result = fns.reduce(
				(acc, { type, fn }) =&gt; {
					if (acc.passed === false) return acc;
					if (type === &#34;filter&#34;) {
          		acc.passed = fn(acc.val)  
							return acc
					}
					if (type === &#34;map&#34;) {
						acc.val = fn(acc.val)
            return acc
					}
				},
				{ passed: true, val: input }
			);

			if (result.passed === true) sum.push(result.val);
			return sum;
		}, []);
	};

	return {
		filterTR,
		mapTR,
		composeReducer
	};
})();

