    print(&quot;my pi is&quot;, myPi, &quot;, diff=&quot;, abs(myPi - math.pi))

import random

def monte_carlo_pi(n):
    start = 0
    for i in range(n):
        x = random.uniform(-1, 1)
        y = random.uniform(-1, 1)
        if x ** 2 + y ** 2 &lt;= 1:
            start += 1
    return start * 4 &#47; n 

print(monte_carlo_pi(100000000))
