# 定义函数
def function(*n1, **n2):
    for i in n1:
        print(i)

    for i, j in n2.items():
        print(f&quot;{i}:{j}&quot;)

    n = len(n1) + len(n2)
    print(f&quot;参数数量 = {n}&quot;)
    print(f&quot;number:{n1}, others:{n2}&quot;)

# 调用已定义好的函数
function(124, 876, 396, 你好=&quot;hello&quot;, 再见=&quot;Goodbye&quot;)

