import math

def circumference(r:int) -&gt; float:
    &quot;&quot;&quot;计算圆的周长&quot;&quot;&quot;
    c = 2 * math.pi * r
    return c

def area_of_circle(r:int) -&gt; float:
    &quot;&quot;&quot;计算圆的面积&quot;&quot;&quot;
    s = math.pi ** 2 * r
    return s

