# 面向过程编程
#定义讲故事的函数
def tell_story():
    print(&quot;从前有座山，山上有座庙，庙里有个老和尚，老和尚在给小和尚讲故事&quot;)

# 主程序
def main():
    while True:
        tell_story()
        print(&quot;故事讲的是：&quot;)

        user_input = input(&quot;继续查看故事请按任意按键（输入&#39;q&#39;退出）&quot;)
        if user_input.lower() == &#39;q&#39;:
            print(&quot;程序已退出&quot;)
            break

main()

# 面向对象编程
# 定义一个类
class Storytell:
    def tell_story(self):
        print(&quot;从前有座山，山上有座庙，庙里有个老和尚，老和尚在给小和尚讲故事&quot;)

# 主程序
def main():
    storytell = Storytell()

    while True:
        storytell.tell_story()
        print(&quot;故事讲的是：&quot;)

        user_input = input(&quot;继续查看故事请按任意按键（输入&#39;q&#39;退出）&quot;)
        if user_input.lower() == &#39;q&#39;:
            print(&quot;程序已退出&quot;)
            break

main()
