&#47;&#47; t2-1.ts

enum Transportations { motorcycle, car, truck }

abstract class T {
    abstract type: Transportations;
    abstract run(): void;
}

class T1 extends T {
    type = Transportations.motorcycle as const;
    constructor(public make: number) {
        super();
    }
    run() {
        console.log(`Running motorcycle with make ${this.make}`);
    }
}

class T2 extends T {
    type = Transportations.car as const;
    constructor(
        public transmission: string,
        public pow: number,
        public id = &#39;T2&#39; as const
    ) {
        super();
    }
    run() {
        console.log(`Running car with transmission ${this.transmission} and power ${this.pow}`);
    }
}

class T3 extends T {
    type = Transportations.truck as const;
    constructor(public capacity: number) {
        super();
    }
    run() {
        console.log(`Running truck with capacity ${this.capacity}`);
    }
}

type Transporter = T;

class MyController {
    run(x: Transporter) {
        x.run();
    }
}

