function bucketSort(dict, min) {
  // åˆ†é…æ¡¶å†…å­˜
  const buckets = new Array(BUCKETS_SIZE);
  for (const url in dict) {
    const count = dict[url];
    // è®°å½•è®¿é—®æ¬¡æ•°ä¸æœ€å°å€¼çš„åç§»ä½œä¸ºå­˜å‚¨ä¸‹æ ‡
    const index = count - min;
