#include <stdio.h>
#include <string.h>
#include <errno.h>
#include <sys/ipc.h>
#include <sys/shm.h>

int getShm();

int main(int argc, char *argv[]){
    getShm();
}

int getShm()
{
    int shmid;
    char path[100] = &#34;/APP/testdir&#34;;
    shmid = shmget(ftok(path, 0x6666), sizeof(int), 0666|IPC_CREAT);
    if(shmid <= 0)
    {
        printf(&#34;shmget %s error! [%d][%s]\n&#34;, path, errno, strerror(errno) );
        return -1;
    }else{
        printf(&#34;success :%s %d %d\n&#34;, __FILE__, __LINE__, shmid);
    }

    return 0;
}


///////////////////Dockerfile//////////////
FROM ubuntu:16.04
COPY ./testdir /APP/testdir
CMD /APP/testdir/main

docker run --rm test_shmget

