&lt;!DOCTYPE html&gt;
&lt;html lang=&#34;en&#34;&gt;
&lt;head&gt;
    &lt;meta charset=&#34;UTF-8&#34;&gt;
    &lt;title&gt;动效小测试&lt;/title&gt;
&lt;/head&gt;
&lt;body&gt;
    &lt;p&gt;变化前-1&lt;/p&gt;
    &lt;button&gt;文本变换&lt;/button&gt;
    &lt;script&gt;
        const btn = document.querySelector(&#34;button&#34;)
        const p = document.querySelector(&#34;p&#34;)
        const value = p.innerText
        btn.addEventListener(&#34;click&#34;, () =&gt; {
            p.innerText = p.innerText === value ? &#34;变化后-2&#34; : value
        })
    &lt;/script&gt;
&lt;/body&gt;
&lt;/html&gt;
