def get_season(month: int) -&gt; str:
    if 3 &lt;= month &lt;= 5:
        return &quot;春季&quot;
    elif 6 &lt;= month &lt;= 8:
        return &quot;夏季&quot;
    elif 9 &lt;= month &lt;= 11:
        return &quot;秋季&quot;
    elif month == 12 or 1 &lt;= month &lt;= 2:
        return &quot;冬季&quot;
    else:
        return &quot;无效的月份&quot;

