from pynput import keyboard

background = [[0, 0, 0],
              [0, 1, 0],
              [0, 0, 0]]

life = 100


def go_up():
    for i, row in enumerate(background):
        for (j, location) in enumerate(row):
            if location and i &gt; 0:
                background[i][j] = 0
                background[i - 1][j] = 1
                break


def go_left():
    for i, row in enumerate(background):
        for (j, location) in enumerate(row):
            if location and j &gt; 0:
                background[i][j] = 0
                background[i][j - 1] = 1
                break


def go_down():
    moved = False
    for i, row in enumerate(background):
        for (j, location) in enumerate(row):
            if location and i &lt; (len(background) - 1):
                background[i][j] = 0
                background[i + 1][j] = 1
                moved = True
                break
        if moved:
            break


def go_right():
    for i, row in enumerate(background):
        for (j, location) in enumerate(row):
            if location and j &lt; (len(row) - 1):
                background[i][j] = 0
                background[i][j + 1] = 1
                break


def on_release(key):
    global life

    if key == keyboard.KeyCode.from_char(&#39;+&#39;):
        if life &lt; 100:
            life += random.randint(1, (100 - life))
    elif key == keyboard.KeyCode.from_char(&#39;-&#39;):
        if life &gt; 0:
            life -= random.randint(1, life)
    elif key == keyboard.Key.up:
        go_up()
    elif key == keyboard.Key.left:
        go_left()
    elif key == keyboard.Key.down:
        go_down()
    elif key == keyboard.Key.right:
        go_right()

    print(&#39;å½“å‰ä½ç½®ï¼š&#39;)
    for row in background:
        print(row)
