    if key == KeyCode.from_char(&quot;w&quot;):
        movehead(background)
    if key == KeyCode.from_char(&quot;s&quot;):
        moveback(background)
    if key == KeyCode.from_char(&quot;a&quot;):
        moveleft(background)
    if key == KeyCode.from_char(&quot;d&quot;):
        moveright(background)
    if key == KeyCode.from_char(&quot;=&quot;):
        if health &lt; 100:
            health += random.randint(0,100)
            if health &gt; 100:
                health = 100
    if key == KeyCode.from_char(&quot;-&quot;):
        if health &gt; 0:
            health -= random.randint(0,100)
            if health &lt;= 0:
                health = 0
                exit(-1)
    print(health)

