class Hamburg(object):

    # 定义汉堡类
    def __init__(self, vegetable, meat, layer):
        self.vegetable = vegetable
        self.meat = meat
        self.layer = layer

    def add_vegetable_meat(self, piece: int, block: int):
        self.vegetable += piece
        self.meat += block
        self.layer += (piece + block)
        return print(f&quot;蔬菜片数: {self.vegetable}\n肉片块数: {self.meat}\n汉堡包总层数: {self.layer}&quot;)

    def reduce_vegetable_meat(self, piece: int, block: int):
        if self.vegetable &gt; 0 and self.meat &gt; 0:
            self.vegetable -= piece
            self.meat -= block
            self.layer -= (piece + block)
            return print(f&quot;蔬菜片数: {self.vegetable}\n肉片块数: {self.meat}\n汉堡总层数: {self.layer}&quot;)


