class FileAtter:
    # 初始化私有属性
    def __init__(self):
        self.__user_data = None

    @property
    def user_data(self):
        if not(self.__user_data):
            with open(&#39;name.txt&#39;, mode=&#39;r&#39;, encoding=&#39;utf-8&#39;) as f:
                self.__user_data = f.read()
        return self.__user_data

    @user_data.setter
    def user_data(self, attr_values: None):
        self.__user_data = attr_values
        if self.__user_data != None:
            with open(&#39;name.txt&#39;, mode=&#39;a+&#39;, encoding=&#39;utf-8&#39;) as f:
                f.write(attr_values)
        return self.__user_data

