        self.__userData = data

    @property
    # 使用property装饰原函数，装饰后就可以使用setter方法来进行装饰并有更多的功能
    def userData(self):
        if not(self.__userData) :
            with open(&#39;.&#47;userData.txt&#39;,mode=&#39;r&#39;,encoding=&#39;UTF-8&#39;) as f:
                self.__userData = f.read()
        return self.__userData

    # 使用property装饰原函数，使用setter方法来进行装饰
    @userData.setter
    def userData(self, data:None):
        self.__userData = data
        if self.__userData:
            with open(&#39;.&#47;userData.txt&#39;,mode=&#39;w+&#39;,encoding=&#39;UTF-8&#39;) as f:
                f.write(data)
        return self.__userData


