type MapTX&lt;Source, T&gt; = {
    [k in keyof Source]: k extends keyof T ? T[k] : Source[k]
}

type MapMartix2&lt;Source extends any[]&gt; = [[&#39;&#39;, ...Source], ...{
    [k in keyof Source]: k extends `${number}`
        ? [Source[k], ...MapTX&lt;Source, {
            [i in Extract&lt;keyof Source, `${number}`&gt;]:
                Source[k] extends Source[i] ? true : false
          }&gt;]
        : Source[k]
}]

type MapMartix2&lt;Source extends any[]&gt; = {
    [k in keyof Source]: k extends `${number}`
        ? MapTX&lt;Source, {
            [i in Extract&lt;keyof Source, `${number}`&gt;]:
                Source[k] extends Source[i] ? true : false
          }&gt;
        : Source[k]
};

type AddHeaders&lt;Matrix extends any[], Header extends string[]&gt; = [
  [&#39;&#39;, ...Header],
  ...{
    [K in keyof Matrix]: [Header[K &amp; number], ...Matrix[K]]
  }
];

type MatrixWithHeaders&lt;Source extends any[], Header extends string[]&gt; = AddHeaders&lt;MapMartix2&lt;Source&gt;, Header&gt;;

&#47;&#47; Example usage:
type Source = [any, never, unknown, void, null, undefined, 1, &#39;a&#39;, string, true, false, boolean];
type Header = [&#39;any&#39;, &#39;never&#39;, &#39;unknown&#39;, &#39;void&#39;, &#39;null&#39;, &#39;undefined&#39;, &#39;1&#39;, &quot;&#39;a&#39;&quot;, &#39;string&#39;, &#39;true&#39;, &#39;false&#39;, &#39;boolean&#39;];

type Result = MatrixWithHeaders&lt;Source, Header&gt;;

